tinymce.PluginManager.add('clear_br', function(editor, url) {
  // Add a button that opens a window
  editor.addButton('clear_br', {
    type: 'button',
    text: 'Удалить br',
    icon: false,
    onclick: clear_br,
  });


  function clear_br() {
    // Top most window object
    win = editor.windowManager.getWindows()[0];
    $(editor.contentDocument).find('br').remove()
  }
});